#include <stdio.h>
#include <stdlib.h>
#include <ctype.h>
#include <stdbool.h>
#include <string.h>

int main(int iArgC, char **apszArgV){
	char *code = "";
	char *glyph;
	glyph = (char*) malloc(3);

	int i, ascii;
	int iLen = strlen(code);

	for(i = 0; i < iLen; i += 2){
		glyph[0] = code[i];
		glyph[1] = code[i + 1];
		glyph[2] = '\0';


		ascii = (int) strtol(glyph, NULL, 16);


		printf("%c", ascii);

	}

	return 0;

}
